mapper.delete:
		delete from api_ptgame
		
			
				code=#{code}
			
			
				and ename=#{ename}
			
			
				and cname=#{cname}
			
			
				and state=#{state}
			
			
				and type=#{type}
			
			
				and id=#{id}
			
			
				and id in (${ids})

mapper.select:
		select * from api_ptgame a
		
			
				a.code=#{code}
			
			
				and a.ename=#{ename}
			
			
				and a.cname=#{cname}
			
			
				and a.state=#{state}
			
			
				and a.type=#{type}
			
			
				and a.id=#{id}
			

		
		  
              
                order by a.${order}
                         
              
            	order by a.id asc

		select * from api_ptgame a
		
			
				 a.code like '%${code}%'
			
			
				and a.ename like '%${ename}%'
			
			
				and a.cname like '%${cname}%'
			
			
				and a.state like '%${state}%'
			
			
				and a.type like '%${type}%'
			
			
				and a.id=#{id}
			

		
		  
              
                order by a.${order}
                         
              
            	order by a.id asc
              
        
		limit #{pageIndex},#{pageSize}
